import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import ArtistForm from '../components/ArtistForm'; // Import ArtistForm component
import ArtistList from '../components/ArtistList'; // Import ArtistList component

const Artists = () => {
  const [artists, setArtists] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArtists = async () => {
      try {
        const response = await fetch('/api/artists'); // Replace with your API endpoint
        if (!response.ok) {
          throw new Error('Failed to fetch artists');
        }
        const data = await response.json();
        setArtists(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching artists:', error);
        setError('Failed to fetch artists. Please try again later.');
        setLoading(false);
      }
    };

    fetchArtists();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h2>Artists</h2>
      <ul>
        {artists.map((artist) => (
          <li key={artist.id}>
            <Link to={`/artists/${artist.id}`}>{artist.name}</Link>
          </li>
        ))}
      </ul>
      <ArtistForm /> {/* Render ArtistForm component */}
      <ArtistList /> {/* Render ArtistList component */}
    </div>
  );
};

export default Artists;
